const { COMMON, ODATA } = require('../constants/annotation')

const isDraftEnabled = entity => {
  return (
    entity &&
    typeof entity === 'object' &&
    Boolean(entity[ODATA.DRAFT] || entity[COMMON.DRAFT_ROOT.PREP_ACTION] || entity[COMMON.DRAFT_NODE.PREP_ACTION])
  )
}

module.exports = {
  isDraftEnabled
}
